package com.parhamcodeappsgmail.phoneplus;import android.content.Context;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import java.util.ArrayList;import java.util.List;public class viewpageradpC extends FragmentPagerAdapter {    private final List<Fragment> frgList=new ArrayList<>();    private final List<String> titleList=new ArrayList<>();    Context context;    public viewpageradpC(FragmentManager fm) {        super(fm);    }    @Override    public Fragment getItem(int position) {        return frgList.get(position);    }    @Override    public int getCount() {        return titleList.size();    }    @Override    public CharSequence getPageTitle(int position) {        return titleList.get(position);    }    public void addfrg(Fragment fragment, String title){        frgList.add(fragment);        titleList.add(title);    }}